mapper.insert:
insert into cart (cart_no, cart_qty, productOption_productOption_no, product_product_no, cart_createdDate, cart_cklimit,
cart_ckId
users_users_id
)values( (select NVL(MAX(cart_no)+1,0)from cart), #{cart_qty}, #{productOption_productOption_no}, #{product_product_no}, sysdate, sysdate+1
,#{cart_ckId}
,#{users_users_id}
)

mapper.select:
select count(*)
from cart
where cart_ckId=#{cart_ckId} and productOption_productOption_no =#{productOption_productOption_no}

select productOption_no
from productOption
where productOption_type=#{cart_optionContent} and product_product_no=#{product_product_no}

select c.cart_no, c.productOption_productOption_no, c.cart_optionContent, c.cart_qty, p.product_no, p.product_name , p.product_price ,p.product_imgFile, o.productOption_no , o.productOption_type from CART c left outer join PRODUCT p on c.product_product_no = p.product_no left outer join PRODUCTOPTION o on c.productOption_productOption_no = o.productOption_no
cart_ckid = #{cart_ckId}
users_users_id = #{users_users_id}

select count(*)
from cart
where users_users_id = #{users_users_id}
