mapper.insert:

         select coupon_seq.nextval from dual


	insert into coupon(
      	coupon_no, coupon_type, coupon_value, coupon_condition, coupon_createdDate, coupon_expiredDate, users_users_id
	)
	values(
		#{coupon_no}, #{coupon_type}, #{coupon_value}, #{coupon_condition}, #{coupon_createdDate}, #{coupon_expiredDate}, #{users_users_id}
	)

mapper.select:
		select count(*)
   		from coupon
    	where users_users_id = #{usersId}
