mapper.select:

       select rnum, review_no, review_name, review_title, review_contents, review_summary, product_no, review_goodorbad
		 from (
		    select rownum as rnum, review_no, review_name, review_title, review_contents, review_summary, product_no, review_goodorbad
		    from(
		        select review_no, review_name, review_title, review_contents, review_summary, product_no, review_goodorbad
		        from address
		        where product_no=#{product_no}
		        order by review_no desc
		    )
		    where rownum <= #{endRowNo}
		 )
		 where rnum >= #{startRowNo}

		select count(*)
  		from review
  		where product_no=#{productNo}
