mapper.select:

       select rnum, review_no, review_name, review_title, review_contents, review_summary, product_no, review_goodorbad, review_createdDate
		 from (
		    select rownum as rnum, review_no, review_name, review_title, review_contents, review_summary, product_no, review_goodorbad, review_createdDate
		    from(
		        select review_no, review_name, review_title, review_contents, review_summary, product_no, review_goodorbad, review_createdDate
		        from review
		        where product_no=#{productNo}
		        order by review_no desc
		    )
		    where rownum <= #{endRowNo}
		 )
		 where rnum >= #{startRowNo}

		select count(*)
  		from review
  		where product_no=#{productNo}
