mapper.select:

       select rnum, review_no, review_name, review_title, review_contents, product_no, review_goodorbad, review_createdDate, review_rating
		 from (
		    select rownum as rnum, review_no, review_name, review_title, review_contents, product_no, review_goodorbad, review_createdDate, review_rating
		    from(
		        select review_no, review_name, review_title, review_contents, product_no, review_goodorbad, review_createdDate, review_rating
		        from review
		        where product_no=#{productNo}
		        order by review_no desc
		    )
		    where rownum <= #{endRowNo}
		 )
		 where rnum >= #{startRowNo}

		select count(*)
  		from review
  		where product_no=#{productNo}

select review_no
from review
where review_name = #{user_id} and product_no=#{productNo} and review_title = #{title}
